import Head from 'next/head'
import styles from '../styles/Mitgliederbereich.module.css'
import Title from '../components/title/Title.js'
import PageContainer from '../components/container/PageContainer.js'
import ContentContainer from '../components/container/ContentContainer.js'
import SectionContainer from '../components/container/SectionContainer.js'
import Paragraph from '../components/paragraph/Paragraph.js'
import { loadFirebase } from '../lib/db.js'
import { useState, useEffect } from 'react'
import { render } from 'react-dom'

export default function Mitgliederbereich() {

    const [input, setInput] = useState('singvogel');
    const [displayTable, setDisplayTable] = useState(false);
    const [renderTable, setRenderTable] = useState([]);

    let tableData = []

    const fetchData = async () => {
        const db = loadFirebase();
        let result = await new Promise((resolve, reject) => {
            db.firestore()
            .collection('contacts/' + input + '/table')
            .get()
            .then(snapshot => { 
                let data = []
                snapshot.forEach(doc => {
                    data.push(doc.data())
                })
                resolve(data)
            })
            .catch(error => {
                reject([])
            })
        })
        return result
    };

    const handleClick = async (event) => {
        event.preventDefault();
        if(input.length > 2 ) {
            tableData = await fetchData();
            if(await tableData[0] != undefined) {
                setRenderTable(tableData);
                console.log(tableData);
                setDisplayTable(true);
            }
        }
    };

    const headerNames = ["Nachname", "Vorname", ""]
    const varNames = ["nachname", "vorname", ""]
    console.log(varNames);
  return (
    <div>
    <PageContainer withFooter={true} menuTypeLanding={false} menuVisibility={true}>
      <ContentContainer>
        <SectionContainer>
            <Title title="Mitgliederbereich" caps={false} />
                <input value={input} onChange={e => setInput(e.target.value)}/>
                <button onClick={handleClick}>Login</button>
    <div className={styles.table}>
        <div className={styles.heading_row}>
            <p className={styles.row_element} style={{width: "250px"}}>Nachname</p>
            <p className={styles.row_element} style={{width: "250px"}}>Vorname</p>
            <p className={styles.row_element} style={{width: "400px"}}>Straße</p>
            <p className={styles.row_element} style={{width: "150px"}}>PLZ</p>
            <p className={styles.row_element} style={{width: "250px"}}>Ort</p>
            <p className={styles.row_element} style={{width: "250px"}}>Telefon</p>
            <p className={styles.row_element} style={{width: "400px"}}>E-Mail</p>
        </div>
        {/*Geht durch die einzelnen Kontakte*/}
        {displayTable ? renderTable.map((tableElement, index) =>
        <div className={styles.row_parent} key={`Key${index}`}>
            <div>
                <p>Nachname</p>
                <p>{tableElement.nachname}</p>
            </div>
        </div>
        )
        : 
        <p>Tabelle wird nicht gezeigt.</p>}
    </div>
        </SectionContainer>
      </ContentContainer>
    </PageContainer>
    </div>
  )
}


