import Head from 'next/head'
import Title from '../components/title/Title.js'
import PageContainer from '../components/container/PageContainer.js'
import ContentContainer from '../components/container/ContentContainer.js'
import SectionContainer from '../components/container/SectionContainer.js'
import Paragraph from '../components/paragraph/Paragraph.js'
import { loadFirebase } from '../lib/db.js'
import { useState, useEffect } from 'react'

Mitgliederbereich.getInitialProps = async (ctx) => {
    const db = loadFirebase()
    let data = []
    const password = loadTable();
    const querySnapshot = await db.firestore().collection('contacts/' + 'singvogel/' + password.toString()).get();
    console.log('contacts/' + 'singvogel/' + password.toString())
    querySnapshot.forEach(doc => {
      data.push(doc.data())
    })
    return {
      data
    }
  }

function loadTable(password) {
    if(password != undefined) {
    return password;
    }
    return "table"
} 


export default function Mitgliederbereich({ data }) {

    const [input, setInput] = useState('singvogel');

    // Test if data is undefinded
    console.log(data[0].name)
    console.log(input)

  return (
    <div>
    <PageContainer withFooter={true} menuTypeLanding={false} menuVisibility={true}>
      <ContentContainer>
        <SectionContainer>
            <Title title="Mitgliederbereich" caps={false} />

            <input value={input} onChange={e => setInput(e.target.value)}/>
                <button onClick={() => loadTable(input)}>Login</button>

        </SectionContainer>
      </ContentContainer>
    </PageContainer>
    </div>
  )
}
