import Head from 'next/head'
import styles from './Slider.module.css'
import { useState, useEffect } from 'react'
import { motion } from 'framer-motion'

export default function Slider() {

  const [isFocused, setFocusedElement] = useState(0);
  console.log(isFocused);

  const variants = {
    content: {
      open: { y: 0, opacity: 1, zIndex: 400},
      closed: { y: "100vh", opacity: 0, zIndex: 400, transition: {
        when: "afterChildren",
      }, },
    }
  }

  const [isToggled, setToggled] = useState(false);
  const toggleTrueFalse = () => setToggled(!isToggled);

  return (
    <div>
        <div className={styles.time_slider}>
          <div className={styles.line}>
            {/* Mappen einen Dot f√ºr jedes Plakat */}
            <div className={styles.dot} onMouseEnter={() => setFocusedElement(1)} onMouseLeave={() => setFocusedElement(0)} style={{backgroundColor: isFocused == 1 ? "red" : "seagreen"}}></div>
            <div className={styles.dot} onMouseEnter={() => setFocusedElement(2)} onMouseLeave={() => setFocusedElement(0)} style={{backgroundColor: isFocused == 2 ? "red" : "seagreen"}}></div>
            <div className={styles.dot}></div>
            <div className={styles.dot}></div>
            <div className={styles.dot}></div>
            <div className={styles.dot}></div>
          </div>
          <div className={styles.values_bottom}>
            <p className={styles.left_value}>Jetzt</p>
            <p className={styles.right_value}>1990</p>
          </div>

        </div>
        <div className={styles.img_slider}>
          <div onClick={toggleTrueFalse} onMouseEnter={() => setFocusedElement(1)} onMouseLeave={() => setFocusedElement(0)} style={{backgroundColor: isFocused == 1 ? "red" : "seagreen"}} className={styles.slide_element}></div>
          <div onMouseEnter={() => setFocusedElement(2)} onMouseLeave={() => setFocusedElement(0)} style={{backgroundColor: isFocused == 2 ? "red" : "seagreen"}} className={styles.slide_element}></div>

          <motion.div initial="closed" animate={isToggled ? "open" : "closed"} transition={{ ease: "easeInOut", duration: 0.2 }} variants={variants.content} className={styles.content_wrapper}>
          <div>
            <p className={styles.left_value}>Jetzt</p>
            <p className={styles.right_value}>1990</p>
          </div>
        </motion.div>
        </div>
    </div>
  )
}
