import styles from './Slider.module.css'
import { motion } from 'framer-motion'
import { useState, useEffect } from 'react'

export default function SliderElement(props) {

    const onClick = () => {
        isFocused = props.id;
        props.onClickHandler;

    }

  return (
    <div>
        <div onClick={props.isDragging == false ? onClick : null} onMouseEnter={() => props.setFocusedElement(props.id)} onMouseLeave={() => props.setFocusedElement(0)} style={{backgroundColor: props.isFocused == props.id ? "red" : "seagreen"}} className={styles.slide_element}></div>
    </div>
  )
}
