import styles from './SliderElement.module.css'
import { motion } from 'framer-motion'
import { useState, useEffect } from 'react'

export default function SliderElement(props) {

    const onClick = () => {
        console.log("run")
        props.setFocusedElement(props.dataElement.id);
        props.onClickHandler();
    }

    const variants = {
        basic: {
          enter: { y: 0, opacity: 1 },
          exit: { y: 50, opacity: 0 }
        },
      };

  return (
    <div>
        <motion.div style={{border: props.isFocused == props.dataElement.id ? "2px solid white" : "2px solid rgba(0,0,0,0)"}} onClick={props.isDragging == false ? onClick : null} onMouseEnter={() => props.setFocusedElement(props.dataElement.id)} onMouseLeave={() => props.setFocusedElement(-1)} className={styles.slide_element}>
        <img draggable="false" className={styles.cover} src={props.dataElement.img} alt="Cover Img" />
        </motion.div>
    </div>
  )
}
